mapper.select:
SELECT a.bunyang_seq ,a.payment_division ,(SELECT code_name FROM TB_COM_CODE cc WHERE cc.code_seq = a.payment_division) payment_division_name ,a.payment_type ,(SELECT code_name FROM TB_COM_CODE cc WHERE cc.parent_code_seq = a.payment_division and cc.code_seq = a.payment_type) payment_type_name ,a.payment_method ,a.payment_amount ,DATE_FORMAT(a.payment_date, '%Y-%m-%d %H:%i:%s') AS payment_date ,DATE_FORMAT(a.payment_date, '%Y-%m-%d') AS payment_date_short ,a.payment_user ,a.create_user ,(SELECT user_name FROM TB_COM_USER cu where cu.user_id = a.create_user) create_user_name ,DATE_FORMAT(a.create_date, '%Y-%m-%d') AS create_date ,(SELECT SUM(payment_amount) FROM TB_BUNYANG_PAYMENT_HISTORY s WHERE s.bunyang_seq = a.bunyang_seq AND s.payment_type = a.payment_type) total_amount FROM TB_BUNYANG_PAYMENT_HISTORY a WHERE bunyang_seq = #{bunyangSeq}
and payment_type = #{paymentType}
ORDER BY a.payment_date, a.create_date
