mapper.select:
SELECT SQL_CALC_FOUND_ROWS a.bunyang_seq ,b.bunyang_no ,a.payment_division ,(SELECT code_name FROM TB_COM_CODE cc WHERE cc.code_seq = a.payment_division) payment_division_name ,a.payment_type ,(SELECT code_name FROM TB_COM_CODE cc WHERE cc.parent_code_seq = a.payment_division and cc.code_seq = a.payment_type) payment_type_name ,a.payment_amount ,a.payment_method ,DATE_FORMAT(a.payment_date, '%Y-%m-%d') AS payment_date ,a.payment_user ,a.create_user ,(SELECT cu.user_name FROM TB_COM_USER cu WHERE cu.user_id = a.create_user) create_user_name ,DATE_FORMAT(a.create_date, '%Y-%m-%d') AS create_date ,remarks FROM TB_BUNYANG_PAYMENT_HISTORY a INNER JOIN TB_BUNYANG_INFO b ON a.bunyang_seq = b.bunyang_seq WHERE 1=1
AND b.bunyang_no = #{bunyangNo}
AND a.payment_type = #{paymentType}
AND payment_date >= STR_TO_DATE(#{fromDt}, '%Y%m%d')
AND payment_date < DATE_ADD(STR_TO_DATE(#{toDt}, '%Y%m%d'), INTERVAL 1 DAY)
ORDER BY a.payment_date DESC, a.bunyang_seq
LIMIT #{start}, #{count}
