mapper.select:
SELECT
	COUNT(1) as total_count 
FROM
(SELECT
	a.bunyang_seq
FROM TB_BUNYANG_PAYMENT_HISTORY a LEFT OUTER JOIN TB_BUNYANG_INFO b ON a.bunyang_seq = b.bunyang_seq
WHERE 1=1

AND b.bunyang_no = #{bunyangNo}


AND a.payment_type = #{paymentType}


AND a.payment_division = #{paymentDivision}



AND payment_date >= STR_TO_DATE(#{fromDt}, '%Y%m%d')




AND payment_date < DATE_ADD(STR_TO_DATE(#{toDt}, '%Y%m%d'), INTERVAL 1 DAY)


) t

SELECT COUNT(1) as total_count FROM (SELECT a.bunyang_seq ,a.bunyang_no ,a.product_type ,a.bunyang_times ,CASE WHEN a.progress_status = 'C' THEN 1 ELSE 2 END as display_order ,(SELECT code_name FROM TB_COM_CODE cc WHERE cc.code_seq = a.product_type AND cc.parent_code_seq = 'PRODUCT_TYPE') product_type_name ,a.couple_type_count ,a.single_type_count ,a.service_charge_type ,(SELECT code_name FROM TB_COM_CODE cc WHERE cc.code_seq = a.service_charge_type AND cc.parent_code_seq = 'SERVICE_CHARGE_TYPE') service_charge_type_name ,a.progress_status ,b.user_id apply_user_id ,b.user_name apply_user_name ,b.relation_type apply_user_relation ,c.user_id agent_user_id ,c.user_name agent_user_name ,c.relation_type agent_user_relation ,(SELECT code_name FROM TB_COM_CODE cc WHERE cc.code_seq = c.relation_type AND cc.parent_code_seq = 'USER_RELATION') agent_user_relation_name ,d.user_id use_user_id ,d.user_name use_user_name ,d.relation_type use_user_relation ,(SELECT code_name FROM TB_COM_CODE cc WHERE cc.code_seq = d.relation_type AND cc.parent_code_seq = 'USER_RELATION') use_user_relation_name ,d.cnt use_user_cnt ,a.regist_user_id ,(SELECT user_name FROM TB_COM_USER cu WHERE cu.user_id = a.regist_user_id) regist_user_name ,DATE_FORMAT(a.regist_date, '%Y-%m-%d') AS regist_date ,(COALESCE(a.couple_type_count,0)*2 + COALESCE(a.single_type_count,0)) * COALESCE((select code_value from TB_COM_CODE where parent_code_seq = 'BUNYANG_TIMES' and code_seq = a.bunyang_times), a.price_per_count) AS total_price ,CASE WHEN progress_status = 'D' THEN 'Y' ELSE 'N' END AS approval_yn ,(SELECT DATE_FORMAT(update_date, '%Y-%m-%d') FROM TB_BUNYANG_HISTORY bh WHERE bh.bunyang_seq = a.bunyang_seq and bh.progress_status = 'B') contract_date ,(SELECT DATE_FORMAT(update_date, '%Y-%m-%d') FROM TB_BUNYANG_HISTORY bh WHERE bh.bunyang_seq = a.bunyang_seq and bh.progress_status = 'C') full_payment_date ,(SELECT DATE_FORMAT(update_date, '%Y-%m-%d') FROM TB_BUNYANG_HISTORY bh WHERE bh.bunyang_seq = a.bunyang_seq and bh.progress_status = 'D') use_approval_date FROM TB_BUNYANG_INFO a INNER JOIN (SELECT bunyang_seq ,user_id ,CASE WHEN user_id IS NULL THEN user_name ELSE (SELECT user_name FROM TB_COM_USER b WHERE a.user_id = b.user_id) END user_name ,ref_type ,relation_type FROM TB_BUNYANG_REF_USER a WHERE ref_type = 'APPLY_USER' ) b ON a.bunyang_seq = b.bunyang_seq LEFT OUTER JOIN (SELECT bunyang_seq ,user_id ,CASE WHEN user_id IS NULL THEN user_name ELSE (SELECT user_name FROM TB_COM_USER b WHERE a.user_id = b.user_id) END user_name ,ref_type ,relation_type FROM TB_BUNYANG_REF_USER a WHERE ref_type = 'AGENT_USER' ) c ON a.bunyang_seq = c.bunyang_seq INNER JOIN (SELECT * ,(SELECT COUNT(*) FROM TB_BUNYANG_REF_USER RU WHERE RU.bunyang_seq = t.bunyang_seq and RU.ref_type = 'USE_USER') cnt FROM (SELECT bunyang_seq ,user_id ,user_name ,relation_type ,CASE WHEN @vbunyang_seq = a.bunyang_seq THEN @rownum:=@rownum+1 ELSE @rownum:=1 END rn ,(@vbunyang_seq := a.bunyang_seq) vbunyang_seq FROM (SELECT bunyang_seq ,user_id ,CASE WHEN user_id IS NULL THEN user_name ELSE (SELECT user_name FROM TB_COM_USER b WHERE a.user_id = b.user_id) END user_name ,relation_type FROM TB_BUNYANG_REF_USER a WHERE a.ref_type = 'USE_USER' ) a, (SELECT @vbunyang_seq:='', @rownum:=0 FROM DUAL) b ORDER BY bunyang_seq, CASE WHEN relation_type = 'ONESELF' THEN 1 ELSE 2 end, user_name ) t WHERE rn = 1 ) d ON a.bunyang_seq = d.bunyang_seq WHERE a.progress_status in ('C', 'D') ) t WHERE 1=1
AND apply_user_name like CONCAT('%',#{apply_user_name},'%')
AND bunyang_times = #{bunyangTimes}
AND progress_status = #{progressStatus}

SELECT COUNT(1) as total_count FROM (SELECT a.bunyang_seq ,a.bunyang_no ,a.product_type ,a.bunyang_times ,CASE WHEN a.progress_status = 'C' THEN 1 ELSE 2 END as display_order ,(SELECT code_name FROM TB_COM_CODE cc WHERE cc.code_seq = a.product_type AND cc.parent_code_seq = 'PRODUCT_TYPE') product_type_name ,a.couple_type_count ,a.single_type_count ,a.service_charge_type ,(SELECT code_name FROM TB_COM_CODE cc WHERE cc.code_seq = a.service_charge_type AND cc.parent_code_seq = 'SERVICE_CHARGE_TYPE') service_charge_type_name ,a.progress_status ,b.user_id apply_user_id ,b.user_name apply_user_name ,b.relation_type apply_user_relation ,c.user_id agent_user_id ,c.user_name agent_user_name ,c.relation_type agent_user_relation ,(SELECT code_name FROM TB_COM_CODE cc WHERE cc.code_seq = c.relation_type AND cc.parent_code_seq = 'USER_RELATION') agent_user_relation_name ,d.user_id use_user_id ,d.user_name use_user_name ,d.relation_type use_user_relation ,(SELECT code_name FROM TB_COM_CODE cc WHERE cc.code_seq = d.relation_type AND cc.parent_code_seq = 'USER_RELATION') use_user_relation_name ,d.cnt use_user_cnt ,d.approval_user_cnt ,a.regist_user_id ,(SELECT user_name FROM TB_COM_USER cu WHERE cu.user_id = a.regist_user_id) regist_user_name ,DATE_FORMAT(a.regist_date, '%Y-%m-%d') AS regist_date ,(COALESCE(a.couple_type_count,0)*2 + COALESCE(a.single_type_count,0)) * COALESCE((select code_value from TB_COM_CODE where parent_code_seq = 'BUNYANG_TIMES' and code_seq = a.bunyang_times), a.price_per_count) AS total_price ,CASE WHEN progress_status = 'D' THEN 'Y' ELSE 'N' END AS approval_yn ,(SELECT DATE_FORMAT(update_date, '%Y-%m-%d') FROM TB_BUNYANG_HISTORY bh WHERE bh.bunyang_seq = a.bunyang_seq and bh.progress_status = 'B') contract_date ,(SELECT DATE_FORMAT(update_date, '%Y-%m-%d') FROM TB_BUNYANG_HISTORY bh WHERE bh.bunyang_seq = a.bunyang_seq and bh.progress_status = 'C') full_payment_date ,(SELECT DATE_FORMAT(update_date, '%Y-%m-%d') FROM TB_BUNYANG_HISTORY bh WHERE bh.bunyang_seq = a.bunyang_seq and bh.progress_status = 'D') use_approval_date FROM TB_BUNYANG_INFO a INNER JOIN (SELECT bunyang_seq ,user_id ,user_name ,ref_type ,relation_type FROM TB_BUNYANG_REF_USER a WHERE ref_type = 'APPLY_USER' ) b ON a.bunyang_seq = b.bunyang_seq LEFT OUTER JOIN (SELECT bunyang_seq ,user_id ,user_name ,ref_type ,relation_type FROM TB_BUNYANG_REF_USER a WHERE ref_type = 'AGENT_USER' ) c ON a.bunyang_seq = c.bunyang_seq INNER JOIN (SELECT * ,(SELECT COUNT(*) FROM TB_BUNYANG_REF_USER RU WHERE RU.bunyang_seq = t.bunyang_seq and RU.ref_type = 'USE_USER') cnt ,(SELECT COUNT(*) FROM TB_BUNYANG_REF_USER RU WHERE RU.bunyang_seq = t.bunyang_seq and RU.ref_type = 'USE_USER' and approval_no is not null) approval_user_cnt FROM (SELECT bunyang_seq ,user_id ,user_name ,relation_type ,CASE WHEN @vbunyang_seq = a.bunyang_seq THEN @rownum:=@rownum+1 ELSE @rownum:=1 END rn ,(@vbunyang_seq := a.bunyang_seq) vbunyang_seq FROM (SELECT bunyang_seq ,user_id ,user_name ,relation_type FROM TB_BUNYANG_REF_USER a WHERE a.ref_type = 'USE_USER' ) a, (SELECT @vbunyang_seq:='', @rownum:=0 FROM DUAL) b ORDER BY bunyang_seq, CASE WHEN relation_type = 'ONESELF' THEN 1 ELSE 2 end, user_name ) t WHERE rn = 1 ) d ON a.bunyang_seq = d.bunyang_seq WHERE a.progress_status in ('C', 'D') ) t WHERE 1=1 AND approval_user_cnt > 0
AND apply_user_name like CONCAT('%',#{apply_user_name},'%')
AND bunyang_times = #{bunyangTimes}
AND progress_status = #{progressStatus}

SELECT COUNT(1) as total_count FROM TB_BUNYANG_MAINT_PAYMENT_INFO t1 INNER JOIN TB_BUNYANG_REF_USER t2 ON t1.bunyang_seq = t2.bunyang_seq AND t1.user_seq = t2.user_seq AND t2.ref_type = 'USE_USER' INNER JOIN TB_BUNYANG_REF_USER t3 ON t1.bunyang_seq = t3.bunyang_seq AND t3.ref_type = 'APPLY_USER' INNER JOIN TB_BUNYANG_INFO t4 ON t1.bunyang_seq = t4.bunyang_seq WHERE 1=1 and exists(select 1 from TB_BUNYANG_REF_USER ru where ru.ref_type = 'USE_USER' and ru.bunyang_seq = t1.bunyang_seq and ru.user_seq = t1.user_seq and ru.cancel_seq is null) and t4.progress_status not in ('E', 'R') AND (t4.cancel_yn is null or t4.cancel_yn != 'Y')
AND t1.bunyang_seq = #{bunyangSeq}
AND t1.maint_year = #{maintYear}
AND t1.payment_yn = #{paymentYn}
