mapper.select:
SELECT t.* ,COUNT(*) OVER() total_count FROM (SELECT a.bunyang_seq ,COALESCE(a.progress_status,'') || a.bunyang_seq bunyang_no ,a.product_type ,(SELECT code_name FROM tb_com_code cc WHERE cc.code_seq = a.product_type AND cc.parent_code_seq = 'PRODUCT_TYPE') product_type_name ,a.couple_type_count ,a.single_type_count ,a.service_charge_type ,(SELECT code_name FROM tb_com_code cc WHERE cc.code_seq = a.service_charge_type AND cc.parent_code_seq = 'SERVICE_CHARGE_TYPE') service_charge_type_name ,a.progress_status ,b.user_id apply_user_id ,b.user_name apply_user_name ,b.relation_type apply_user_relation ,c.user_id agent_user_id ,c.user_name agent_user_name ,c.relation_type agent_user_relation ,(SELECT code_name FROM tb_com_code cc WHERE cc.code_seq = c.relation_type AND cc.parent_code_seq = 'USER_RELATION') agent_user_relation_name ,d.user_id use_user_id ,d.user_name use_user_name ,d.relation_type use_user_relation ,(SELECT code_name FROM tb_com_code cc WHERE cc.code_seq = d.relation_type AND cc.parent_code_seq = 'USER_RELATION') use_user_relation_name ,d.cnt use_user_cnt ,a.regist_user_seq ,(SELECT user_name FROM tb_com_user cu WHERE cu.user_id = a.regist_user_seq) regist_user_name ,TO_CHAR(a.regist_date, 'YYYY-MM-DD') AS regist_date FROM tb_bunyang_info a INNER JOIN (SELECT bunyang_seq ,user_id ,CASE WHEN user_id IS NULL THEN user_name ELSE (SELECT user_name FROM tb_com_user b WHERE a.user_id = b.user_id) END user_name ,ref_type ,relation_type FROM tb_bunyang_ref_user a WHERE ref_type = 'APPLY_USER' ) b ON a.bunyang_seq = b.bunyang_seq LEFT OUTER JOIN (SELECT bunyang_seq ,user_id ,CASE WHEN user_id IS NULL THEN user_name ELSE (SELECT user_name FROM tb_com_user b WHERE a.user_id = b.user_id) END user_name ,ref_type ,relation_type FROM tb_bunyang_ref_user a WHERE ref_type = 'AGENT_USER' ) c ON a.bunyang_seq = c.bunyang_seq INNER JOIN (SELECT * FROM (SELECT bunyang_seq ,user_id ,user_name ,relation_type ,rank() over(partition BY bunyang_seq ORDER BY CASE WHEN relation_type = 'ONESELF' THEN 1 ELSE 2 end, user_name) rn ,COUNT(*) over(partition BY bunyang_seq) cnt FROM (SELECT bunyang_seq ,user_id ,CASE WHEN user_id IS NULL THEN user_name ELSE (SELECT user_name FROM tb_com_user b WHERE a.user_id = b.user_id) END user_name ,relation_type FROM tb_bunyang_ref_user a WHERE a.ref_type = 'USE_USER' ) a ) t WHERE rn = 1 ) d ON a.bunyang_seq = d.bunyang_seq WHERE A.progress_status in ('N', 'A', 'E') ) t WHERE 1=1
AND apply_user_name like CONCAT('%',#{apply_user_name},'%')
ORDER BY t.bunyang_seq LIMIT #{countPerPage} OFFSET (#{pageIndex}-1)*#{countPerPage}

mapper.update:
UPDATE tb_bunyang_info a
file_seq_apply=#{file_seq_apply}, file_seq_use_user=#{file_seq_use_user}, file_seq_approval=#{file_seq_approval}, file_seq_use_approval=#{file_seq_use_approval}, file_seq_contract=#{file_seq_contract}
WHERE a.bunyang_seq = #{bunyangSeq}

UPDATE tb_bunyang_info a
	SET progress_status = #{progressStatus}
WHERE a.bunyang_seq = #{bunyangSeq}
